class App extends React.Component {
    state = {
        paintings: null
    };

    ReadDataFromDB() {
        console.log("mounted");
        db.collection('paintings')
            .get()
            .then(snapshot => {
                const paintings = [];
                snapshot.forEach(doc => {
                    const data = doc.data();
                    console.log(data);
                    if (data)
                        paintings.push(data)
                })
                this.setState({paintings: paintings})

            }).catch(err => console.log("error => " + err))
    }

    render() {
        // console.log( "renderr => "+this.state.paintings)
        return (
            <div className="app">
				<Header/>
				<Painting/>
				<Footer/>

				<main>
                    <h1>list of paintings</h1>{
                        this.state.paintings && this.state.paintings.map(paintings => {
                            return (
                                <div>
                                    <section className="gallery">
                                        <Card title={paintings.name}
                                              description="Kantanka Onantefo I, a 4Ã—4 cross country vehicle"
                                              url="http://africarm.org/the-story-behind-kantanka-onantefo-1876/"
                                              img="http://africarm.org/site/wp-content/uploads/2013/07/Kantanka-onantefo.jpg"/>
                                    </section>
                                </div>
                            )
                        })
                    }
                </main>
            </div>
        )
    }
}


