import React from 'react';
import logo from './logo.svg';
import './App.css';
import {db} from './service/firebase'
import {Car} from './painting-extra-details/painting-extra-details'

class App extends React.Component {
    state = {
        paintings: null
    };

    constructor(props) {
        super(props);
        this.ReadDataFromDB();
        console.log(this.state.paintings);
    }

    ReadDataFromDB() {
        // console.log("mounted");

        db.collection('paintings')
            .get()
            .then(snapshot => {
                const paintings = [];
                snapshot.forEach(doc => {
                    const data = doc.data();
                    // console.log(data);
                    if (data)
                        paintings.push(data)
                });
                this.setState({paintings: paintings});
                // console.log(paintings);

                // return paintings;

            }).catch(err => console.log("error => " + err))
    }

    render() {
        // console.log( "renderr => "+this.state.paintings)
        return (
            <div className="app">
                <Header/>
                <Paintings/>
                <Footer/>
            </div>
        )
    }
}


// function Appp() {
//     return (
//         <div className="App">
//             <Header/>
//             <Cars/>
//             <Footer/>
//         </div>
//     );
// }

function Header() {
    return (
        <header><h2>List Of Paintings</h2></header>
    );
}

function Footer() {
    return (
        <footer>We Are The Best</footer>
    );
}

function Paintings() {
    if (this.state.paintings) {
        this.state.paintings && this.state.paintings.map(paintings => {
            return (
                <div>
                    <section className="gallery">
                        <Card title={paintings.name}
                              description="Kantanka Onantefo I, a 4×4 cross country vehicle"
                              url="http://africarm.org/the-story-behind-kantanka-onantefo-1876/"
                              img="http://africarm.org/site/wp-content/uploads/2013/07/Kantanka-onantefo.jpg"/>
                    </section>
                </div>
            )
        })
    }
}

function Card(props) {
    return (
        <div className="Card">
            <h5 className="card-title">{props.title}</h5>
            <div className="card-body">
                <img className="card-img-top" src={props.img} alt=""/>
                <p className="card-text">{props.description}</p>
                <a href={props.url} className="btn btn-primary">Details</a>
            </div>
        </div>
    );
}

export default App;
