import React from "react";
import './Painting-extra-details.scss';

class PaintingExtraDetails extends React.Component {

    render() {
        return (this.Paint());
    }

    Paint() {
        return (
            <div style={{display: "inline-block"}}>
                <div className="paint">
                    <div>
                        <h5 className="text-color">{this.props.data.title}</h5>
                        <img className="img" src={this.props.data.img} alt=""/>
                    </div>

                    <div>
                        <p className="text-color"><b>Artist:</b> {this.props.data.artist}</p>
                        <p className="text-color"><b>Description:</b> {this.props.data.description}</p>
                        <br/>
                    </div>
                    <button className="btn btn-info">Buy Now</button>
                </div>
            </div>
        )
    }
}

export default PaintingExtraDetails
