import React from "react";
class Paintings extends React.Component {

    constructor(props) {
        super(props);
        this.state = {data: this.props.data};
    }

    render() {
        console.log(this.props.data);
        if (this.props.data) {
            //return (<p>Work</p>)
            this.props.data.paintings && this.props.data.paintings.map(painting => {
                return (
                    <div>
                        <section className="gallery">
                            {this.Card()}
                            {/*<Card title={painting.name}*/}
                            {/*      description="Kantanka Onantefo I, a 4Ã—4 cross country vehicle"*/}
                            {/*      url="http://africarm.org/the-story-behind-kantanka-onantefo-1876/"*/}
                            {/*      img="http://africarm.org/site/wp-content/uploads/2013/07/Kantanka-onantefo.jpg"/>*/}
                        </section>
                    </div>
                )
            })
        } else {
            return (<p>DB Empty</p>)
        }
    }

    Card() {
        return (
            <div className="Card">
                <h5 className="card-title">{this.state.title}</h5>
                <div className="card-body">
                    <img className="card-img-top" src={this.state.img} alt=""/>
                    <p className="card-text">{this.state.description}</p>
                    <a href={this.state.url} className="btn btn-primary">Details</a>
                </div>
            </div>
        );
    }
}

export default Paintings;
