import React from "react";
import './Paintings.scss';
import '.././painting-extra-details/Painting-extra-details.js'
class Paintings extends React.Component {

    constructor(props) {
        super(props);
    }

    // state = {
    //     flag === 1 ? < : null >
    // };

    render() {
        return (this.List());
    }

    List() {
        if (this.props.data) {
            console.log(this.props.data);
            return (
                this.props.data.map((paint) => (
                        <div style={{display: "inline-block"}}>
                            {this.Card(paint)}
                        </div>
                    )
                )
            )
        } else {
            return (<p>DB Empty</p>)
        }
    }

    Card(paint) {
        return (
            <div className="Card">
                <div className="card-body">
                    <h5 className="card-title">{paint.title}</h5>
                    <img className="card-img-top" src={paint.img} alt=""/>
                    <div class = "text-below-image">
                        <p className="text"><b>Artist:</b> {paint.artist}</p>
                        <p className="text"><b>Description:</b> {paint.description}</p>
                        <br/>
                    </div>
                    <button className="btn btn-info">Extra Details</button>
                </div>
            </div>
        );
    }
}

export default Paintings;
