import React from "react";
import App from "../App";

class Paintings extends React.Component {

    constructor() {
        super();
        this.state = {data: this.props}
        console.log(this.state.data);
    }

    Paintings(props) {
        console.log(props.state);
        if (props.state) {
            //return (<p>Work</p>)
            props.state.paintings && props.state.paintings.map(painting => {
                return (
                    <div>
                        <section className="gallery">
                            <Card title={painting.name}
                                  description="Kantanka Onantefo I, a 4Ã—4 cross country vehicle"
                                  url="http://africarm.org/the-story-behind-kantanka-onantefo-1876/"
                                  img="http://africarm.org/site/wp-content/uploads/2013/07/Kantanka-onantefo.jpg"/>
                        </section>
                    </div>
                )
            })
        } else {
            return (<p>DB Empty</p>)
        }
    }
}

function Card(props) {
    return (
        <div className="Card">
            <h5 className="card-title">{props.title}</h5>
            <div className="card-body">
                <img className="card-img-top" src={props.img} alt=""/>
                <p className="card-text">{props.description}</p>
                <a href={props.url} className="btn btn-primary">Details</a>
            </div>
        </div>
    );
}

export default Paintings;
