import React from 'react';
import logo from './logo.svg';
import './App.css';
import {db} from './service/firebase'
import {Car} from './painting-extra-details/painting-extra-details'

class App extends React.Component {
    state = {
        paintings: null
    };

    constructor(props) {
        super(props);
        this.ReadDataFromDB();
    }

    ReadDataFromDB() {
        // console.log("mounted");

        db.collection('paintings')
            .get()
            .then(snapshot => {
                const paintings = [];
                snapshot.forEach(doc => {
                    const data = doc.data();
                    // console.log(data);
                    if (data)
                        paintings.push(data)
                });
                this.setState({paintings: paintings});
                console.log(this.state.paintings);
                this.render();


                // console.log(paintings);

                // return paintings;

            }).catch(err => console.log("error => " + err))
    }

    render() {
        // console.log( "renderr => "+this.state.paintings)
        return (
            <div className="app">
                <Header/>
                <Paintings state={this.state}/>
                <Footer/>
            </div>
        )
    }
}


// function Appp() {
