import React from "react";
import './Paintings.scss';
import '.././painting-extra-details/Painting-extra-details.js'
import PaintingExtraDetails from "../painting-extra-details/Painting-extra-details";
import {bool} from "prop-types";
// import {BrowserRouter as Router, Switch, Route} from "react-router-dom";
// import { StackNavigator } from 'react-navigation';



class Paintings extends React.Component {


    constructor(props) {
        super(props);
    }

    state = {
        flag: false
    };

    render() {
        console.log(this.state.flag);
        return (
            <div>
                { this.state.flag === true ? <PaintingExtraDetails/> : this.List() }
                 {/*{ this.List() }*/}
            </div>
        );
    }

    List() {
        if (this.props.data) {
            console.log(this.props.data);
            return (
                this.props.data.map((paint) => (
                        <div style={{display: "inline-block"}}>
                            {this.Card(paint)}
                        </div>
                    )
                )
            )
        } else {
            return (<p>DB Empty</p>)
        }
    }

    Card(paint) {
        return (
            <div className="Card">
                <div className="card-body">
                    <h5 className="card-title">{paint.title}</h5>
                    <img className="card-img-top" src={paint.img} alt=""/>
                    <div class="text-below-image">
                        <p className="text"><b>Artist:</b> {paint.artist}</p>
                        <p className="text"><b>Description:</b> {paint.description}</p>
                        <br/>
                    </div>

                    <button className="btn btn-info"
                            onClick={() => this.setState({flag: true})}
                    >Extra Details
                    </button>
                </div>
            </div>
        );
    }
}

export default Paintings;
