import React from 'react';
import './App.css';
import {db} from './service/firebase'
import Paintings from "./paintings/Paintings.js";

class App extends React.Component {
    state = {
        paintings:null
    };

    constructor(props) {
        super(props);
    }

    componentDidMount() {
        db.collection('paintings')
            .get()
            .then(snapshot => {
                const paintingsList = [];
                snapshot.forEach(doc => {
                    const data = doc.data();
                    // console.log(data);
                    if (data)
                        paintingsList.push(data)
                });
                this.setState({paintings: paintingsList});
                console.log(this.state.paintings);
                // window.location.reload();
                // this.render();
                // console.log(paintings);
            }).catch(err => console.log("error => " + err))
    }

    render() {
        // console.log( "renderr => "+this.state.paintings)
        return (
            <div className="app">
                <Header/>
                <Paintings data={this.state.paintings}/>
                <Footer/>
            </div>
        )
    }
}

function Header() {
    return (
        <header class="header"><h2>List Of Paintings</h2></header>
    );
}

function Footer() {
    return (
        <footer>We Are The Best</footer>
    );
}

export default App;
