import React from "react";

class Paintings extends React.Component {

    constructor(props) {
        super(props);
    }

    state = {
        url: "https://www.motortrend.com/news/toyota-ft-ac-concept-teased-ahead-of-l-a-debut/",
        description: "It's beginning to feel a lot like auto show season.",
        img: "https://st.motortrend.com/uploads/sites/5/2017/04/Toyota-FT-4X-Concept-front-side.jpg",
        title: "Toyota FT-AC Concept",
        imgName:"Leonardo",
        price: 200,
        artist: "Leonardo"
    };

    render() {
        return (this.List());
    }

    List() {
        if (this.props.data) {
            // return (<p>Work</p>)
            return (
                this.props.data.map((paint) => (
                        <div>
                            <p>{paint.name}</p>
                            {this.Card(paint)}
                        </div>
                    )
                )
            )
            //     return (
            //         <div>
            //             <p>Work</p>
            //             {/*<section className="gallery">*/}
            //             {/*    {this.Card()}*/}
            //             {/*    /!*<Card title={painting.name}*!/*/}
            //             {/*    /!*      description="Kantanka Onantefo I, a 4×4 cross country vehicle"*!/*/}
            //             {/*    /!*      url="http://africarm.org/the-story-behind-kantanka-onantefo-1876/"*!/*/}
            //             {/*    /!*      img="http://africarm.org/site/wp-content/uploads/2013/07/Kantanka-onantefo.jpg"/>*!/*/}
            //             {/*</section>*/}
            //         </div>
            //     )
            // })
        } else {
            return (<p>DB Empty</p>)
        }
    }

    Card(paint) {
        return (
            <div className="Card" style={{display:"inline"}}>
                <h5 className="card-title">{paint.title}</h5>
                <div className="card-body">
                    <img className="card-img-top" src={this.state.img} alt=""/>
                    <p className="card-text">{paint.description}</p>
                    <p className="card-text">{this.state.imgName}</p>
                    <a href={this.state.url} className="btn btn-primary">Details</a>
                </div>
            </div>
        );
    }
}
    //
    // description: "It's beginning to feel a lot like auto show season.",
    // img: "https://st.motortrend.com/uploads/sites/5/2017/04/Toyota-FT-4X-Concept-front-side.jpg",
    // title: "Toyota FT-AC Concept",
    // imgName:"Leonardo",
    // price: 200,
    // artist: "Leonardo"

export default Paintings;
